<ul>\n					<li><span style=\"font-size:16px\">-9223372036854775808 ~ 9223372036854775807</span></li>\n				</ul>\n				</li>\n			</ul>\n			</li>\n			<li><span style=\"font-size:16px\">unsigned long long int</span>\n			<ul>\n				<li><span style=\"font-size:16px\">8 byte</span>\n				<ul>\n					<li><span style=\"font-size:16px\">0 ~ 18446744073709551615</span></li>\n				</ul>\n				</li>\n			</ul>\n			</li>\n		</ol>\n		</li>\n		<li><span style=\"font-size:16px\"><strong>float</strong></span>\n		<ol>\n			<li><span style=\"font-size:16px\">float &nbsp;</span>\n			<ul>\n				<li><span style=\"font-size:16px\">4byte</span></li>\n			</ul>\n			</li>\n			<li><span style=\"font-size:16px\">double </span>\n			<ul>\n				<li><span style=\"font-size:16px\">8byte</span></li>\n			</ul>\n			</li>\n			<li><span style=\"font-size:16px\">long double&nbsp;</span>\n			<ul>\n				<li><span style=\"font-size:16px\">10 byte</span></li>\n				<li><span style=\"font-size:16px\">16 byte</span>\n				<ul>\n					<li>7.065x10<sup>-9865</sup>..1.415x10<sup>9864</sup>&nbsp;(18 sf或33 sf)</li>\n				</ul>\n				</li>\n			</ul>\n			</li>\n		</ol>\n		</li>\n		<li><strong><span style=\"font-size:16px\">char</span></strong>\n		<ol>\n			<li><span style=\"font-size:16px\">char </span>\n			<ul>\n				<li><span style=\"font-size:16px\">1 byte</span></li>\n				<li><span style=\"font-size:16px\">-128 ~ +127</span></li>\n			</ul>\n			</li>\n			<li><span style=\"font-size:16px\">unsigned char</span>\n			<ul>\n				<li><span style=\"font-size:16px\">1 byte&nbsp;</span></li>\n				<li><span style=\"font-size:16px\">0 ~ 255</span></li>\n			</ul>\n			</li>\n			<li><span style=\"font-size:16px\">signed char&nbsp;</span>\n			<ul>\n				<li><span style=\"font-size:16px\">1 byte</span></li>\n				<li><span style=\"font-size:16px\">-128 ~ + 127</span></li>\n			</ul>\n			</li>\n		</ol>\n		</li>\n		<li><strong><span style=\"font-size:16px\">size_t&nbsp; (</span></strong><a href=\"https://www.cnblogs.com/jeff_nie/archive/2010/10/09/184685