y no desc&quot;;\n    st = con.createStatement();\n    rs = st.executeQuery(sql);\n\n    while (rs.next()){\n        Map&lt;String,Object&gt; item = new HashMap&lt;String , Object&gt;();\n        item.put(&quot;no&quot; , rs.getString(&quot;no&quot;));\n        item.put(&quot;content&quot; , rs.getString(&quot;content&quot;));\n        items.add(item);\n    }\n\n    st.close();\n    rs.close();\n    con.close();\n\n} catch (SQLException e) {\n    e.printStackTrace();\n    Log.e(TAG, &quot;show data connected mysql fail.&quot;);\n}\n\nreturn items;</pre>\n		<br />\n		&nbsp;</li>\n	</ul>\n	</li>\n	<li><span style=\"font-size:14px\"><span style=\"color:#0000FF\">jdbc connect mysql</span></span>\n	<ul>\n		<li><span style=\"font-size:14px\"><span style=\"color:#0000FF\">​​​​​</span></span>\n		<pre>\n<span style=\"font-size:14px\">import android.util.Log;\n\nimport java.sql.Connection;\nimport java.sql.DriverManager;\nimport java.sql.ResultSet;\nimport java.sql.SQLException;\nimport java.sql.Statement;\n\npublic class mysql {\n\n\n    String ip  = &quot;db4free.net&quot;;\n    int port   = 3306;\n    String db  = &quot;toolsapp&quot;;\n    String url = &quot;jdbc:mysql://&quot; + ip + &quot;:&quot; + port + &quot;/&quot; + db + &quot;?useSSL=false&quot;;\n    String user = &quot;backup123&quot;;\n    String pwd  = &quot;KpTuz@cfL5C3XR8&quot;;\n\n    /*\n    String ip  = &quot;118.150.53.44&quot;;\n    int port   = 3306;\n    String db  = &quot;ToolApp&quot;;\n    String url = &quot;jdbc:mysql://&quot; + ip + &quot;:&quot; + port + &quot;/&quot; + db;\n    String user = &quot;backup&quot;;\n    String pwd  = &quot;SLbackup#123&quot;;\n\n     */\n    Connection con;\n    String TAG = &quot;mysql msg&quot;;\n\n\n    public void run(){\n        try{\n            Class.forName(&quot;com.mysql.cj.jdbc.Driver&quot;);\n            Log.e(TAG, &quot;run: load mysql driver success.&quot;);\n        }catch (ClassNotFoundException e){\n            Log.e(TAG, &quot;run: load mysql driver fail.&quot;);\n            return;\n        }\n\n\n        try{\n            con = DriverManager.getConnection(url,user,pwd);\n            Log.e(TAG, &quot;run: connected mysql success.&quot;);\n        }catch (SQLException e){\n            Log.e(TAG, &quot;run: connected mysql fail.&quot;);\n        }\n\n\n    }\n\n    public String get_data(){\n        String data = &quot;&quot;;\n        try {\n            con = DriverManager.getConnection(url , user , pwd);\n            String sql = &quot;select * from test&quot;;\n            Statement st = con.createStatement();\n            ResultSet rs = st.executeQuery(sql);\n\n            while (rs.next()){\n                String kind = rs.getString(&quot;id&quot;);\n                String date = rs.getString(&quot;name&quot;);\n                data += kind + &quot; / &quot; + date + &quot;\n&quot;;\n            }\n\n            st.close();\n\n        }catch (SQLException e) {\n            e.printStackTrace();\n        }\n        return data;\n    }\n\n    public String get_data2(){\n        String data = &quot;&quot;;\n\n        try{\n            con = DriverManager.getConnection(url , user , pwd);\n            String sql = &quot;select * from test order by id desc&quot;;\n            Statement st = con.createStatement();\n            ResultSet rs = st.executeQuery(sql);\n\n            while(rs.next()){\n                String id = rs.getString(&quot;id&quot;);\n                String name = rs.getString(&quot;name&quot;);\n                data += id + &quot; / &quot; + name + &quot;\n&quot;;\n            }\n\n            st.close();\n\n        }catch (SQLException e){\n            e.printStackTrace();\n        }\n\n        return data;\n    }\n\n    public void insert_data(String name){\n        try{\n            con = DriverManager.getConnection(url , user , pwd);\n            String sql = &quot;insert into test(name) values(&#39;&quot;+name+&quot;&#39;)&quot;;\n            Statement st = con.createStatement();\n   